import React from 'react';
import { Card, CardMedia, CardContent, Typography, Button, Box } from '@mui/material';

const BookListItem = ({ book, onAdd }) => (
  <Card style={{ position: 'relative', margin: '10px' }}>
    <CardMedia
      component="img"
      alt={book.title}
      height="250"
      image={`${process.env.PUBLIC_URL}${book.coverPhotoURL}`}
      title={book.title}
    />
    <CardContent style={{ position: 'absolute', bottom: '0', background: 'rgba(0, 0, 0, 0.5)', color: 'white', width: '100%' }}>
      <Typography variant="h6">{book.title}</Typography>
      <Typography variant="body2">{book.author}</Typography>
      <Button variant="contained" color="primary" onClick={onAdd} style={{ marginTop: '10px' }}>
        Add to Reading List
      </Button>
    </CardContent>
  </Card>
);

export default BookListItem;
