import React from 'react';
import { ApolloProvider } from '@apollo/client';
import client from './apolloClient';
import BookAssignment from './pages/BookAssignment';
import { CssBaseline, Container, Typography, Box } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import '@fontsource/mulish';
import MenuBar from './components/MenuBar'; // Import the MenuBar component

const theme = createTheme({
  typography: {
    fontFamily: 'Mulish, sans-serif',
  },
});

function App() {
  return (
    <ApolloProvider client={client}>
      <ThemeProvider theme={theme}>
        <CssBaseline />
        <MenuBar /> {/* Include the MenuBar component here */}
        <Container style={{ paddingTop: '80px' }}> {/* Add padding at the top */}
          <Box
            sx={{
              backgroundColor: '#f0f0f0', // Set your desired background color
              padding: '20px', // Add padding
              borderRadius: '15px', // Rounded corners
              textAlign: 'center', // Center the text
              boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', // Add a subtle shadow for a nice effect
            }}
          >
            <Typography variant="h3" component="h1" gutterBottom>
              Book Assignment View
            </Typography>
          </Box>
          <BookAssignment />
        </Container>
      </ThemeProvider>
    </ApolloProvider>
  );
}

export default App;
