import React, { useState } from "react";
import BookListItem from "./BookListItem";

const BookList = ({ books }) => {
  const [displayedBooks, setDisplayedBooks] = useState(books.slice(0, 3));
  const [loadMoreVisible, setLoadMoreVisible] = useState(true);

  const handleLoadMore = () => {
    const nextBatch = books.slice(displayedBooks.length, displayedBooks.length + 3);
    setDisplayedBooks([...displayedBooks, ...nextBatch]);
    if (displayedBooks.length + 3 >= books.length) {
      setLoadMoreVisible(false);
    }
  };

  return (
    <>
      {displayedBooks.map((book, index) => (
        <BookListItem key={index} book={book} />
      ))}
      {loadMoreVisible && (
        <button onClick={handleLoadMore}>Load More</button>
      )}
    </>
  );
};

export default BookList;
