import React from "react";
import { Grid, Card, CardMedia, CardContent, Typography, Button } from "@mui/material";

const BookListItem = ({ book, onAdd }) => {
  const imageUrl = `${process.env.PUBLIC_URL}${book.coverPhotoURL}`;

  return (
    <Grid item xs={6} sm={4} md={3} lg={2}> {/* Each item takes up 6 columns on extra small screens, 4 columns on small screens, 3 columns on medium screens, and 2 columns on large screens */}
      <Card style={{ position: "relative", margin: "10px" }}>
        <CardMedia
          component="img"
          height="200"
          image={imageUrl}
          onError={() => console.error(`Failed to load image: ${imageUrl}`)}
        />
        <CardContent style={{ textAlign: "center" }}>
          <Typography variant="h8" style={{ marginBottom: "5px" }}>
            {book.title}
          </Typography>
          <Typography
            variant="body2"
            sx={{ fontStyle: "italic", marginBottom: "5px" }}
          >
            by {book.author}
          </Typography>
          <Button
            variant="contained"
            style={{ backgroundColor: '#54CCCC', color: 'white'}}
            onClick={onAdd}
          >
            Add to Reading List
          </Button>
        </CardContent>
      </Card>
    </Grid>
  );
};

export default BookListItem;
