import React, { useState } from 'react';
import { useQuery, gql } from '@apollo/client';
import { TextField, Typography, Grid, Box, Tab, Tabs, AppBar } from '@mui/material';
import BookListItem from '../components/BookListItem';
import ReadingList from '../components/ReadingList';

const GET_BOOKS = gql`
  query Books {
    books {
      author
      coverPhotoURL
      title
    }
  }
`;

const BookAssignment = () => {
  const { loading, error, data } = useQuery(GET_BOOKS);
  const [searchTerm, setSearchTerm] = useState('');
  const [readingList, setReadingList] = useState([]);
  const [tabValue, setTabValue] = useState(0);

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  const handleAddBook = (book) => {
    setReadingList([...readingList, book]);
  };

  const handleRemoveBook = (title) => {
    setReadingList(readingList.filter(book => book.title !== title));
  };

  const filteredBooks = data.books.filter(book =>
    book.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Box>
      <TextField
        label="Search for books"
        variant="outlined"
        fullWidth
        size="small"
        margin="normal"
        sx={{ borderRadius: '15px' }} // Rounded corners
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <AppBar position="static">
        <Tabs value={tabValue} onChange={handleTabChange} aria-label="Tabs">
          <Tab label="All Books" />
          <Tab label="Reading List" />
        </Tabs>
      </AppBar>
      {tabValue === 0 && (
        <Grid container spacing={2}>
          {filteredBooks.map((book, index) => (
            <Grid item xs={12} sm={6} md={3} key={index}>
              <BookListItem
                book={book}
                onAdd={() => handleAddBook(book)}
              />
            </Grid>
          ))}
        </Grid>
      )}
      {tabValue === 1 && (
        <ReadingList readingList={readingList} onRemove={handleRemoveBook} />
      )}
    </Box>
  );
};

export default BookAssignment;
