import React, { useState } from 'react';
import { useQuery, gql } from '@apollo/client';
import { TextField, Typography, Grid, Box } from '@mui/material';
import BookListItem from '../components/BookListItem';
import ReadingListItem from '../components/ReadingListItem';

const GET_BOOKS = gql`
  query Books {
    books {
      author
      coverPhotoURL
      title
    }
  }
`;

const BookAssignment = () => {
  const { loading, error, data } = useQuery(GET_BOOKS);
  const [searchTerm, setSearchTerm] = useState('');
  const [readingList, setReadingList] = useState([]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  const handleAddBook = (book) => {
    setReadingList([...readingList, book]);
  };

  const handleRemoveBook = (title) => {
    setReadingList(readingList.filter(book => book.title !== title));
  };

  const filteredBooks = data.books.filter(book =>
    book.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Box>
      <TextField
        label="Search for books"
        variant="outlined"
        fullWidth
        size="small"
        margin="normal"
        sx={{ borderRadius: '15px' }} // Rounded corners
        onChange={(e) => setSearchTerm(e.target.value)}
      />
       <Typography variant="h5" component="h2" gutterBottom style={{ marginTop: '20px' }}>
        Reading List
      </Typography>
      <Grid container spacing={2}>
        {readingList.map((book, index) => (
          <Grid item xs={12} sm={6} md={3} key={index}>
            <ReadingListItem
              book={book}
              onRemove={() => handleRemoveBook(book.title)}
            />
          </Grid>
        ))}
      </Grid>
      <Grid container spacing={2}>
        {filteredBooks.map((book, index) => (
          <Grid item xs={12} sm={6} md={3} key={index}>
            <BookListItem
              book={book}
              onAdd={() => handleAddBook(book)}
            />
          </Grid>
        ))}
      </Grid>
     
    </Box>
  );
};

export default BookAssignment;
