import React, { useState } from "react";
import { useQuery, gql } from "@apollo/client";
import { TextField, Typography, Grid, Box, Button } from "@mui/material";
import BookListItem from "../components/BookListItem";
import ReadingListItem from "../components/ReadingListItem";
import ConfirmationDialog from "../components/ConfirmationDialog"; // Import the custom confirmation dialog component

const GET_BOOKS = gql`
  query Books {
    books {
      author
      coverPhotoURL
      title
    }
  }
`;

const BookAssignment = () => {
  const { loading, error, data } = useQuery(GET_BOOKS);
  const [searchTerm, setSearchTerm] = useState("");
  const [readingList, setReadingList] = useState([]);
  const [confirmationDialogOpen, setConfirmationDialogOpen] = useState(false);
  const [bookToRemove, setBookToRemove] = useState(null);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  const handleAddBook = (book) => {
    // Check if the book is already in the reading list
    if (!readingList.some((item) => item.title === book.title)) {
      setReadingList([...readingList, book]);
    } else {
      alert("This book is already in your reading list.");
    }
  };

  const handleRemoveBook = (title) => {
    setBookToRemove(title);
    setConfirmationDialogOpen(true);
  };

  const confirmRemoveBook = () => {
    setReadingList(readingList.filter((book) => book.title !== bookToRemove));
    setConfirmationDialogOpen(false);
  };

  const handleCloseDialog = () => {
    setConfirmationDialogOpen(false);
  };

  const filteredBooks = data.books.filter((book) =>
    book.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Box>
      <TextField
        label="Search for books"
        variant="outlined"
        fullWidth
        size="small"
        margin="normal"
        sx={{ borderRadius: "15px" }}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      {readingList.length > 0 && (
        <>
          <Typography
            variant="h5"
            component="h2"
            gutterBottom
            style={{ marginTop: "20px" }}
          >
            Reading List
          </Typography>
          <Grid container spacing={2}>
            {readingList.map((book, index) => (
              <Grid item xs={12} sm={6} md={3} key={index}>
                <ReadingListItem
                  book={book}
                  onRemove={() => handleRemoveBook(book.title)}
                />
              </Grid>
            ))}
          </Grid>
        </>
      )}

      <Typography
        variant="h5"
        component="h2"
        gutterBottom
        style={{ marginTop: "20px" }}
      >
        All Books
      </Typography>
      <Grid container spacing={2}>
        {filteredBooks.map((book, index) => (
          <Grid item xs={12} sm={6} md={3} key={index}>
            <BookListItem book={book} onAdd={() => handleAddBook(book)} />
          </Grid>
        ))}
      </Grid>

      {/* Confirmation dialog for removing a book */}
      <ConfirmationDialog
        open={confirmationDialogOpen}
        onClose={handleCloseDialog}
        onConfirm={confirmRemoveBook}
        title="Confirm Removal"
        content={`Are you sure you want to remove "${bookToRemove}" from your reading list?`}
      />
    </Box>
  );
};

export default BookAssignment;
