import React, { useState } from 'react';
import { ApolloProvider } from '@apollo/client';
import client from './apolloClient';
import BookAssignment from './pages/BookAssignment';
import ReadingList from './components/ReadingList'; // Import the ReadingList component
import { CssBaseline, Container, Typography, Tabs, Tab } from '@mui/material';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import '@fontsource/mulish';

const theme = createTheme({
  typography: {
    fontFamily: 'Mulish, sans-serif',
  },
});

function App() {
  const [tabIndex, setTabIndex] = useState(0);
  const [readingList, setReadingList] = useState([]); // Initialize reading list state

  const handleTabChange = (event, newValue) => {
    setTabIndex(newValue);
  };

  return (
    <ApolloProvider client={client}>
      <ThemeProvider theme={theme}>
        <CssBaseline />
        <Container style={{ paddingTop: '80px' }}>
          <Typography variant="h4" component="h1" gutterBottom>
            Book Assignment View
          </Typography>
          <Tabs value={tabIndex} onChange={handleTabChange} centered>
            <Tab label="All Books" />
            <Tab label="Reading List" />
          </Tabs>
          {tabIndex === 0 && <BookAssignment />}
          {tabIndex === 1 && <ReadingList readingList={readingList} />} {/* Render ReadingList only when second tab is active */}
        </Container>
      </ThemeProvider>
    </ApolloProvider>
  );
}

export default App;
