import React, { useState } from 'react';
import { useQuery, gql } from '@apollo/client';
import { TextField, Typography, Grid, Box } from '@mui/material';
import BookListItem from '../components/BookListItem';
import ReadingListItem from '../components/ReadingListItem';
import ConfirmationDialog from '../components/ConfirmationDialog';

const GET_BOOKS = gql`
  query Books {
    books {
      author
      coverPhotoURL
      title
    }
  }
`;

const BookAssignment = () => {
  const { loading, error, data } = useQuery(GET_BOOKS);
  const [searchTerm, setSearchTerm] = useState('');
  const [readingList, setReadingList] = useState([]);
  const [confirmationDialogOpen, setConfirmationDialogOpen] = useState(false);
  const [dialogTitle, setDialogTitle] = useState('');
  const [dialogContent, setDialogContent] = useState('');
  const [bookToRemove, setBookToRemove] = useState(null);

  const handleAddBook = (book) => {
    // Check if the book is already in the reading list
    if (!readingList.some((item) => item.title === book.title)) {
      setReadingList([...readingList, book]);
    } else {
      // Show confirmation dialog if the book is already in the reading list
      setDialogTitle('Duplicate Book');
      setDialogContent('This book is already in your reading list.');
      setConfirmationDialogOpen(true);
    }
  };

  const handleRemoveBook = (title) => {
    // Show confirmation dialog before removing the book
    setDialogTitle('Remove Book');
    setDialogContent(`Are you sure you want to remove "${title}" from your reading list?`);
    setBookToRemove(title);
    setConfirmationDialogOpen(true);
  };

  const confirmRemoveBook = () => {
    const updatedList = readingList.filter((book) => book.title !== bookToRemove);
    setReadingList(updatedList);
    setBookToRemove(null);
    setConfirmationDialogOpen(false);
  };

  const filteredBooks = data.books.filter((book) =>
    book.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Box>
      <TextField
        label="Search for books"
        variant="outlined"
        fullWidth
        size="small"
        margin="normal"
        sx={{ borderRadius: '15px' }}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      {readingList.length > 0 && (
        <>
          <Typography
            variant="h5"
            component="h2"
            gutterBottom
            style={{ marginTop: '20px' }}
          >
            Reading List
          </Typography>
          <Grid container spacing={2}>
            {readingList.map((book, index) => (
              <Grid item xs={12} sm={6} md={3} key={index}>
                <ReadingListItem
                  book={book}
                  onRemove={() => handleRemoveBook(book.title)}
                />
              </Grid>
            ))}
          </Grid>
        </>
      )}

      <Typography
        variant="h5"
        component="h2"
        gutterBottom
        style={{ marginTop: '20px' }}
      >
        All Books
      </Typography>
      <Grid container spacing={2}>
        {filteredBooks.map((book, index) => (
          <Grid item xs={12} sm={6} md={3} key={index}>
            <BookListItem book={book} onAdd={() => handleAddBook(book)} />
          </Grid>
        ))}
      </Grid>

      <ConfirmationDialog
        open={confirmationDialogOpen}
        onClose={() => setConfirmationDialogOpen(false)}
        title={dialogTitle}
        content={dialogContent}
        onConfirm={confirmRemoveBook}
      />
    </Box>
  );
};

export default BookAssignment;
