import React, { useState } from 'react';
import { useQuery, gql } from '@apollo/client';
import { TextField, Typography, Grid, Box, Tab, Tabs, AppBar } from '@mui/material';
import BookListItem from '../components/BookListItem';

const GET_BOOKS = gql`
  query Books {
    books {
      author
      coverPhotoURL
      title
    }
  }
`;

const BookAssignment = () => {
  const { loading, error, data } = useQuery(GET_BOOKS);
  const [searchTerm, setSearchTerm] = useState('');
  const [addedBooks, setAddedBooks] = useState([]); // State to store added books

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;

  const handleAddBook = (book) => {
    setAddedBooks([...addedBooks, book]); // Add book to addedBooks state
  };

  const handleRemoveBook = (title) => {
    setAddedBooks(addedBooks.filter(book => book.title !== title)); // Remove book from addedBooks state
  };

  const filteredBooks = data.books.filter(book =>
    book.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <Box>
      <AppBar position="static">
        <Tabs>
          <Tab label="All Books">
            <TextField
              label="Search for books"
              variant="outlined"
              fullWidth
              size="small"
              margin="normal"
              sx={{ borderRadius: '15px' }}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <Grid container spacing={2}>
              {filteredBooks.map((book, index) => (
                <Grid item xs={12} sm={6} md={3} key={index}>
                  <BookListItem
                    book={book}
                    onAdd={() => handleAddBook(book)}
                  />
                </Grid>
              ))}
            </Grid>
          </Tab>
          <Tab label="Reading List">
            <Grid container spacing={2}>
              {addedBooks.map((book, index) => (
                <Grid item xs={12} sm={6} md={3} key={index}>
                  <BookListItem
                    book={book}
                    onRemove={() => handleRemoveBook(book.title)}
                  />
                </Grid>
              ))}
            </Grid>
          </Tab>
        </Tabs>
      </AppBar>
    </Box>
  );
};

export default BookAssignment;
