import React, { useState } from "react";
import {
  Card,
  CardMedia,
  CardContent,
  Typography,
  Button,
  Grid,
} from "@mui/material";

const BookListItem = ({ book, onAdd }) => {
  const imageUrl = `${process.env.PUBLIC_URL}${book.coverPhotoURL}`;

  return (
    <Grid item xs={12} sm={6} md={4} lg={3}>
      <Card style={{ position: "relative", margin: "10px" }}>
        <CardMedia
          component="img"
          height="250"
          image={imageUrl}
          onError={() => console.error(`Failed to load image: ${imageUrl}`)} // Log error if image fails to load
        />
        <CardContent style={{ textAlign: "center" }}>
          <Typography variant="h8" style={{ marginBottom: "5px" }}>
            {book.title}
          </Typography>
          <Typography
            variant="body2"
            sx={{ fontStyle: "italic", marginBottom: "5px" }}
          >
            by {book.author}
          </Typography>
          <Button variant="contained" color="primary" onClick={onAdd}>
            Add to Reading List
          </Button>
        </CardContent>
      </Card>
    </Grid>
  );
};

const BookList = ({ books, onAdd }) => {
  const [visibleBooks, setVisibleBooks] = useState(6); // Number of books initially visible
  const loadMore = () => {
    setVisibleBooks(visibleBooks + 6); // Increase the number of visible books by 6
  };

  return (
    <>
      <Grid container spacing={3}>
        {books.slice(0, visibleBooks).map((book, index) => (
          <BookListItem key={index} book={book} onAdd={onAdd} />
        ))}
      </Grid>
      {visibleBooks < books.length && ( // Display the "Load More" button if there are more books to load
        <Button variant="outlined" color="primary" onClick={loadMore}>
          Load More
        </Button>
      )}
    </>
  );
};

export default BookList;
